sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (( isLoading && !g_form.isNewRecord() ) || newValue === '' || newValue == oldValue) {
        return;
    }
    var userID = g_form.getValue('u_requested_for');
	var getAddr = new GlideAjax('GetEmailAddr');
	getAddr.addParam('sysparm_name', 'getEmail');
	getAddr.addParam('sysparm_userID', userID);
	getAddr.getXML(emailField);

	function emailField(response){
		var email = response.responseXML.documentElement.getAttribute('answer');
		if(email == ''){
			alert("No email for " + userID + ".");
		} else {
			g_form.clearValue('u_requested_for_email');
			g_form.setValue('u_requested_for_email', email);
		}
	}

}
