sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		gs.info("KK! init NeedItUtils");
    },
	isPast: function(aDate, refDate){
		var now = new GlideDateTime(refDate);
		var req = new GlideDateTime(aDate);
		gs.info("KK! compare " +now.toString() +" and " +req.toString());
		if(req.before(now)){
			return true;
		}
		return false;
	}, 
	isToday: function(aDate){
		var now = new GlideDateTime();
		var req = new GlideDateTime(aDate);
		return req.equals(now);
	},
    type: 'NeedItUtils'
};
